import React, { useState, useEffect } from 'react';
import { useHistory } from 'react-router-dom';
import axios from "axios";
import validator from 'validator'

const Register = () => {

    const history = useHistory();

    const [username, setName] = useState("");
    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");

    const [pasval, setpasval] = useState("");
    const [errorMessage, setErrorMessage] = useState('')

    const validate = (value) => {

        if (validator.isStrongPassword(value, {
            minLength: 8, minLowercase: 1,
            minUppercase: 1, minNumbers: 1, minSymbols: 1
        })) {
            setErrorMessage('Is Strong Password')
        } else {
            setErrorMessage('Is Not Strong Password')
        }
    }


    useEffect(() => {
        if (localStorage.getItem('token')) {
            history.push('./home')
        }
    }, [])




    const submit = async (e) => {
        e.preventDefault();


        localStorage.setItem('name', username)
        localStorage.setItem('email', email)
        localStorage.setItem('password', password)
        history.push('/selectbrand')

    }


    return (
        <>
            <form>
                <div className="register-login-section spad">
                    <div className="container">
                        <div className="row">
                            <div className="col-lg-6 offset-lg-3">
                                <div className="register-form">
                                    <h2>Register</h2>
                                    <form action="#">

                                        <div className="group-input">
                                            <label for="username">Username *</label>
                                            <input type="text" minlength="5" value={username} onChange={(e) => setName(e.target.value)} className="form-control" id="inputEmail4" required />
                                        </div>
                                        <div className="group-input">
                                            <label for="email">Email</label>
                                            <input type="email" value={email} onChange={(e) => setEmail(e.target.value)} className="form-control" id="inputEmail4" required />
                                        </div>
                                        <div className="group-input">
                                            <label for="pass">Password *</label>
                                            <input type="password" value={password} onChange={(e) => validate(e.target.value)} className="form-control" id="inputPassword4" required />
                                        </div>
                                        <div className="group-input">
                                            <label for="con-pass">Confirm Password *</label>
                                            <input type="password" id="con-pass" required />
                                        </div>
                                        <span style={{
                                            fontWeight: 'bold',
                                            color: 'red',
                                        }}>{errorMessage}</span>
                                        <button type="submit" onClick={submit} className="site-btn register-btn">REGISTER</button>
                                    </form>
                                    <div className="switch-login">
                                        <a href="login.html" className="or-login">Or Login</a>
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            </form>

        </>
    )
}
export default Register;
