import React, { useState } from 'react';
import { Redirect } from 'react-router-dom';
import { useHistory } from 'react-router-dom';

const Activate = ({match }) => {

    const history = useHistory();

    const [Err, setErr] = useState(false);

  
        const uid = match.params.uid;
        const token = match.params.token;
        console.warn('*****************activation*****************')
        console.warn(uid)
        console.warn(token)

        const submit = async (e) => {
            e.preventDefault();
            
        
            const article = { uid: uid, token: token };
            const response = await  fetch('http://127.0.0.1:8000/auth/users/activation/', {
                method: 'POST',
                headers: { 'Content-Type': 'application/json'
                
            },
                body: JSON.stringify(article)
            });
            if (response.status == 204) {
                return <Redirect to='/' />
            }
            else {
                history.go('/login')
                setErr("Eror login again")
            } 


          }

    return (
        <div className='container'>
            <div 
                className='d-flex flex-column justify-content-center align-items-center'
                style={{ marginTop: '200px' }}
            >
                <h1>Verify your Account:</h1>
                <button type="submit" className="site-btn login-btn" onClick={submit}>Sign In</button>      
            
            </div>
        </div>
    );
};

export default Activate;
