import React, { useState } from 'react';
import { Redirect } from 'react-router-dom';
import { useHistory } from 'react-router-dom';

const Confirmpassword = ({match }) => {

    const history = useHistory();

    const [confirmpassword, setConfirmpassword] = useState("");
    const [password, setPassword] = useState("");
    const [err, setErr] = useState("");
    const [load,setLoad]=useState(false);

  
        const uid = match.params.uid;
        const token = match.params.token;
        console.warn('*****************activation*****************')
        console.warn(uid)
        console.warn(token)

        const submit = async (e) => {
            e.preventDefault();
            
        
            const article = { uid: uid, token: token };
            const response = await  fetch('http://127.0.0.1:8000/auth/users/activation/', {
                method: 'POST',
                headers: { 'Content-Type': 'application/json'
                
            },
                body: JSON.stringify(article)
            }).then((res)=>
            {
                if(res.status==204){
            alert('Account activate Successfully')
                    history.push('/login')
                }
            })

          }

    return (
        <div className="register-login-section spad">
        <div className="container">
            <div className="row">
                <div className="col-lg-6 offset-lg-3">
                    <div className="login-form">
                        <h2>Login</h2>
                        <form action="#">
                        
                            <div className="group-input">
                                <label htmlFor="pass">Password *</label>
                                <input type="password" value={password} onChange={(e) => setPassword(e.target.value)} className="form-control" id="inputPassword4" />
                                <h6 style={{ color: "red" }} >{err}</h6>
                            </div>
                           
                            <div className="group-input">
                                <label htmlFor="pass">Confirm Password *</label>
                                <input type="password" value={confirmpassword} onChange={(e) => setPassword(e.target.value)} className="form-control" id="inputPassword4" />
                                <h6 style={{ color: "red" }} >{err}</h6>
                            </div>
                            <button type="submit" className="site-btn login-btn" onClick={submit} disabled={load}>{load && <i className="fa fa-refresh fa-spin"></i> } Sign In</button>
                        </form>
                        <div className="switch-login">
                            <a href="register" className="or-login">Or Create An Account</a>
                        </div>
                        <br/>
                        <div style={{display: 'flex',  justifyContent:'center', alignItems:'center',background:'#E7AB3C'}}>

                         <GoogleLogin
                         clientId="930967656832-4ihegpm18g9l62tp6d3t90coolia90sb.apps.googleusercontent.com"
                         onSuccess={responseGoogle}
                         onFailure={responseGoogle}
                         cookiePolicy={'single_host_origin'}

                         /> </div>                            </div>
                </div>
            </div>
        </div>
    </div>
    );
};

export default Confirmpassword;
