import Footer from './Footer';
import '../index.css';
import React, { useCallback, useEffect, useState, useRef } from 'react';
import { reactLocalStorage } from "reactjs-localstorage";
import axios from 'axios';

function Home()
{
    const [int_brand, setint_brand] = useState([]);
    const name = localStorage.getItem('name');
    const [brand1, setbrand1] = useState([]);
    const [brand2, setbrand2] = useState([]);
    const [brand3, setbrand3] = useState([]);
    const [loading, setLoading] = useState(false);

    useEffect(() => {
        axios.get('http://127.0.0.1:8000/users/'+name+'/')
            .then(res => {
                console.warn(res.data.user_interest_brands)
                setint_brand(res.data.user_interest_brands);
            })
            .catch((error) => {
                console.log(error)
            })

    }, []);
  

    
    useEffect(() => {
        var str = "" + int_brand
        var item = str.split(',')
        console.warn(item + '********item***********');
        console.warn(item + '********item***********' + item[1]);
        localStorage.setItem("brand1",item[0])
        localStorage.setItem("brand2",item[1])
        localStorage.setItem("brand3",item[2])

    }, [loading])

    setTimeout(() => {
        setLoading(true)
    }, 3000);
    
    // const getUsers = async () => {
    //     console.warn('**********************item at api*****'+item[0])

    //     await axios.get('http://127.0.0.1:8000/brandapi/' + item[0] + '/')
    //         // axios.get('http://127.0.0.1:8000/brandapi/khaadi/')

    //         .then(res => {
    //             console.warn(res)
    //             setbrand1(res.data)

    //         })
    //     axios.get('http://127.0.0.1:8000/brandapi/'+item[1]+'/')
    //     .then(res => {
    //         console.warn(res.data)
    //         setbrand2(res.data)  

    //     })
    //     axios.get('http://127.0.0.1:8000/brandapi/'+item[2]+'/')
    //     .then(res => {
    //         console.warn(res.data)
    //         setbrand3(res.data)  

    //     })
    // }



    return(
        <>
        <h1>heloooe</h1>
            <h5>{brand1.name}</h5>
            <br />
           <h5>{brand2.name}</h5>
            <br/>
            <h5>{brand3.name}</h5>
        <section className="hero-section">
        <div className="hero-items owl-carousel">
            <div className="single-hero-items set-bg" data-setbg="img/hero-1.jpg">
                <div className="container">
                    <div className="row">
                        <div className="col-lg-5">
                            <span>Bag,kids</span>
                            <h1>Black friday</h1>
                            <p>Lorem ipsum dolor sit amet, consectetur adipisicing elit, sed do eiusmod tempor
                                incididunt ut labore et dolore</p>
                            <a href="#" className="primary-btn">Shop Now</a>
                        </div>
                    </div>
                    <div className="off-card">
                        <h2>Sale <span>50%</span></h2>
                    </div>
                </div>
            </div>
            <div className="single-hero-items set-bg" data-setbg="img/hero-2.jpg">
                <div className="container">
                    <div className="row">
                        <div className="col-lg-5">
                            <span>Bag,kids</span>
                            <h1>Black friday</h1>
                            <p>Lorem ipsum dolor sit amet, consectetur adipisicing elit, sed do eiusmod tempor
                                incididunt ut labore et dolore</p>
                            <a href="#" className="primary-btn">Shop Now</a>
                        </div>
                    </div>
                    <div className="off-card">
                        <h2>Sale <span>50%</span></h2>
                    </div>
                </div>
            </div>
        </div>
        {/* <!-- Banner Section Begin --> */}
    <div className="banner-section spad">
        <div className="container-fluid">
            <div className="row">
                <div className="col-lg-4">
                    <div className="single-banner">
                        <img src="img/banner-1.jpg" alt=""/>
                        <div className="inner-text">
                            <h4>Men’s</h4>
                        </div>
                    </div>
                </div>
                <div className="col-lg-4">
                    <div className="single-banner">
                        <img src="img/banner-2.jpg" alt=""/>
                        <div className="inner-text">
                            <h4>Women’s</h4>
                        </div>
                    </div>
                </div>
                <div className="col-lg-4">
                    <div className="single-banner">
                        <img src="img/banner-3.jpg" alt=""/>
                        <div className="inner-text">
                            <h4>Kid’s</h4>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    </div>
    {/* <!-- Banner Section End --> */}
    </section>

       {/* Search by brands */}
    <section className="man-banner spad">
        <div className="container-fluid">
            <div className="row">
                <div className="col-sm">
                    <div className="filter-control">
                        <ul>
                            <li className="active"><span><h2>Your Favourite Brands</h2></span></li>
                            
                        </ul>
                    </div>
                    <div className="product-slider owl-carousel">
                        <div className="product-item">
                            <div className="pi-pic">
                                <img src={brand1.link}  alt=""  />
                                <h3>{brand1.name}</h3>
                                <div className="sale">Sale</div>
                            </div>
                        </div>
                        <div className="product-item">
                            <div className="pi-pic">
                                <img src={brand2.link}  alt=""/>
                                <h3>{brand2.name}</h3>
                                <div className="sale">Sale</div>                                                               
                            </div>
                        </div>
                        <div className="product-item">
                            <div className="pi-pic">
