import React ,{useState,useEffect} from 'react';
import '../index.css';
import axios from 'axios'; 
import Card from './Card';
import { reactLocalStorage } from "reactjs-localstorage";


const Product=props=>
{   
    
    const [users,setUsers]=useState([]);
    const [empty,setEmpty]=useState(false);
    var nameArr = props.name.split('-')
    console.warn('*******************'+nameArr)
    const brand=nameArr[0].toUpperCase();
    const type=nameArr[1].toUpperCase();

    const b_g_c=props.name
    useEffect(()=>{
        async function getData(){
        
            const res=await axios.get('http://127.0.0.1:8000/productapi/'+b_g_c+'/?page=1')

            console.log(res.data.results[0])
            setUsers(res.data.results);

            if(res.data.results[0]==null)
            setEmpty(true)

        }

        getData();
    },[]);


    return (
    <>
    {
            empty==true ? 

            <h3 style={{color:'red'}}>Sory Currently No Sale Available of that Product....</h3>
        :
    <div className ='thirteen'>
    <h1>{brand} {type}</h1>
    </div>
    
    }
    {
    users.map((val)=>{
    return  (
        <>
        <div className='card1'>
        <Card
        key={val.id}
        imgsrc={val.image_link}
        imgsrc2={val.image_link2}

        title={val.title}
        sname={val.brand_name}
        link={val.product_link}
        price={val.price} 
        sale_price={val.sale_price} 
        />
        </div>
        </>
       )
    
    
    }
    
    )}

    
    
    
    </>)
}

export default Product;
