import React, { useState, useEffect } from 'react';
import { useHistory } from 'react-router-dom';
import axios from "axios";
const Register = () => {

    const history = useHistory();

    const [username, setName] = useState("");
    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");

    // useEffect(() => {
    //     if (localStorage.getItem('name')) {
    //         history.push('./home')
    //     }
    // }, [])
   
  
  const submit = async (e) => {
    e.preventDefault();
    let item = { username:username, email:email, password:password };
    const response = await fetch('http://127.0.0.1:8000/api/users/', {
        method: 'POST',
        headers: {'Content-Type': 'application/json'},
        body: JSON.stringify(item)
    });
    console.warn('****************'+response.status)
    if (response.status==201) {
        console.warn("************************Successful****************")
        localStorage.setItem("name",username)
        localStorage.setItem("password",password)
        // history.push('/selectbrand')
      }
      else
      {
        console.warn("Eror login again")
      }


    const content = await response.json();
    console.warn(content)

    
}

    return (
        <>
        <form>
        <div className="register-login-section spad">
        <div className="container">
            <div className="row">
                <div className="col-lg-6 offset-lg-3">
                    <div className="register-form">
                        <h2>Register</h2>
                        <form action="#">
                            <div className="group-input">
                                <label for="username">Username *</label>
                                <input type="text" value={username} onChange={(e) => setName(e.target.value)} className="form-control" id="inputEmail4" />
                            </div>
                            <div className="group-input">
                                <label for="email">Email</label>
                                <input type="text" value={email} onChange={(e) => setEmail(e.target.value)} className="form-control" id="inputEmail4" />
                            </div>
                            <div className="group-input">
                                <label for="pass">Password *</label>
                                <input type="password" value={password} onChange={(e) => setPassword(e.target.value)} className="form-control" id="inputPassword4" />
                            </div>
                            <div className="group-input">
                                <label for="con-pass">Confirm Password *</label>
                                <input type="text" id="con-pass"/>
                            </div>
                            <button type="submit" onClick={submit} className="site-btn register-btn">REGISTER</button>
                        </form>
                        <div className="switch-login">
                            <a href="login.html" className="or-login">Or Login</a>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    </div>
    </form>

        </>
    )
}
export default Register;
