import React, { useState, useEffect } from 'react';
import { useHistory } from 'react-router-dom';
import axios from "axios";
// import { reactLocalStorage } from "reactjs-localstorage";
const Login = () => {
    const history = useHistory();



    // useEffect(() => {
    //     if (localStorage.getItem('token')) {
    //         history.push('./home')
    //     }
    // }, [])


    const [username, setName] = useState("");
    const [password, setPassword] = useState("");
    const [err, setErr] = useState("");

    const submit = async (e) => {
        e.preventDefault();
        const article = { username: username, password: password };
        const response = await fetch('http://127.0.0.1:8000/auth/', {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            credentials: 'include',
            body: JSON.stringify(article)
        });
        const content = await response.json();

        const token = content.token;
        if (response.status == 200) {
            console.warn("************************Successful****************")
            localStorage.setItem("token", token)
            localStorage.setItem("name", username)
            //   history.go('/home')
        }
        else {
            setErr("Eror login again")
        }



    }









    return (
        <>
            {/* <form className="col-md-6 offset-sm-3">
                <h1>User sign in</h1>
                <br />

                <input type="text" value={username} onChange={(e) => setName(e.target.value)} className="form-control" id="inputEmail4" />
                <br />
                <input type="password" value={password} onChange={(e) => setPassword(e.target.value)} className="form-control" id="inputPassword4" />

                <button type="submit" onClick={submit} className="btn btn-primary">Sign up</button>
                <br/>
                <h3 style={{color: "red"}} >{err}</h3>

            </form> */}
            
            <div className="register-login-section spad">
                <div className="container">
                    <div className="row">
                        <div className="col-lg-6 offset-lg-3">
                            <div className="login-form">
                                <h2>Login</h2>
                                <form action="#">
                                    <div className="group-input">
                                        <label for="username">Username *</label>
                                        <input type="text" value={username} onChange={(e) => setName(e.target.value)} className="form-control" id="inputEmail4" />
                                    </div>
                                    <div className="group-input">
                                        <label for="pass">Password *</label>
                                        <input type="password" value={password} onChange={(e) => setPassword(e.target.value)} className="form-control" id="inputPassword4" />
                                        <h3 style={{ color: "red" }} >{err}</h3>
                                    </div>
                                    <div className="group-input gi-check">
                                        <div className="gi-more">
                                            <label for="save-pass">
                                                Save Password
                                        <input type="checkbox" id="save-pass" />
                                                <span className="checkmark"></span>
                                            </label>
                                            <a href="#" className="forget-pass">Forget your Password</a>
                                        </div>
                                    </div>
                                    <button type="submit" className="site-btn login-btn" onClick={submit} className="btn btn-primary">Sign In</button>
                                </form>
                                <div className="switch-login">
                                    <a href="register.html" className="or-login">Or Create An Account</a>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            </div>

        </>
    )
}
export default Login;
