import React ,{useState,useEffect} from 'react';
import '../index.css';
import axios from 'axios'; 
import Card from './Card';

const Product=props=>
{   
    
    const [users,setUsers]=useState([]);
    const [empty,setEmpty]=useState(false);



    const b_g_c=props.name
    useEffect(()=>{
        async function getData(){
        
            const res=await axios.get('http://127.0.0.1:8000/productapi/'+b_g_c+'/?page=1')
            console.log(res.data.results[0])
            setUsers(res.data.results);

            if(res.data.results[0]=='')
            setEmpty(true)

        }

        getData();
    },[]);


    return (<>
    
    <h1>Product details</h1>

    {
    users.map((val)=>{
    return  (
        <>
        {
            localStorage.getItem('name') ? 

            <h3>Sory Currently No Sale Available of that Product</h3>
        :
        <Card
        key={val.id}
        imgsrc={val.image_link}
        imgsrc2={val.image_link2}

        title={val.title}
        sname={val.brand_name}
        link={val.product_link}
        price={val.price} 
        sale_price={val.sale_price} 
        />
        }
        </>
       )
    }
    
    )}
    
    
    
    </>)
}

export default Product;
