import React, { useState, useEffect } from 'react';
import { useHistory } from 'react-router-dom';
import axios from "axios";
const Register = () => {

    const history = useHistory();

    const [username, setName] = useState("");
    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");

    useEffect(() => {
        if (localStorage.getItem('name')) {
            history.push('./home')
        }
    }, [])
   
  
  const submit = async (e) => {
    e.preventDefault();
    let item = { username:username, email:email, password:password };
    const response = await fetch('http://127.0.0.1:8000/api/users/', {
        method: 'POST',
        headers: {'Content-Type': 'application/json'},
        body: JSON.stringify(item)
    });
    console.warn('****************'+response.status)
    if (response.status==201) {
        console.warn("************************Successful****************")
        localStorage.setItem("name",username)
        localStorage.setItem("password",password)
        history.push('/selectbrand')
      }
      else
      {
        console.warn("Eror login again")
      }


    const content = await response.json();
    console.warn(content)

    
}







    return (
        <>
            <form className="col-md-6 offset-sm-3">
                <h1>User sign in</h1>
                <br />

                <input type="text" value={username} onChange={(e) => setName(e.target.value)} className="form-control" id="inputEmail4" />
                <br />

                <input type="text" value={email} onChange={(e) => setEmail(e.target.value)} className="form-control" id="inputEmail4" />

                <input type="password" value={password} onChange={(e) => setPassword(e.target.value)} className="form-control" id="inputPassword4" />

                <button type="submit" onClick={submit} className="btn btn-primary">Sign up</button>

            </form>

        </>
    )
}
export default Register;
