import React ,{useState,useEffect} from 'react';
import '../index.css';
import axios from 'axios'; 
import Card from './Card';

import ReactPaginate from "react-paginate";

const Gender_product= props=>
{   
    
    const [users,setUsers]=useState([]);
    const [pageNumber, setPageNumber] = useState(0);

    const g=props.name

    const usersPerPage =20;
  const pagesVisited = pageNumber * usersPerPage;

    useEffect(()=>{
        async function getData(){
        
            const res=await axios.get('http://127.0.0.1:8000/productapi/'+g+'-Kid-Kid-Kid/')
            console.log(res.data.results)
            setUsers(res.data);

        }

        getData();
    },[]);


    return (<>
    <div className="thirteen">
    <h1>All Product releated to {props.name}</h1>
    </div>

    {
    users.map((val)=>{
    return  (
        <div className='card1'>
        <Card
        key={val.id}
        imgsrc={val.image_link}
        imgsrc2={val.image_link2}

        title={val.title}
        sname={val.brand_name}
        link={val.product_link}
        price={val.price} 
        sale_price={val.sale_price} 
        />
        </div>
       )
    }
    
    )}
    
    
    
    
    
    </>)
}

export default Gender_product;
