import emailjs from "emailjs-com";
import React from 'react';

export default function Testing() {


  const [load,setLoad]=useState(false);


    function sendEmail(e) {
        e.preventDefault();

    emailjs.sendForm('service_nmfea79', 'template_lejaz39', e.target, 'user_ST3KnhWnIqNBJzctmQalH')
        .then((result) => {
            console.log(result.text);
        }, (error) => {
            console.log(error.text);
        });
        e.target.reset()
    }



    const submit = async (e) => {
      setLoad(true);
      e.preventDefault();
      emailjs.sendForm('service_nmfea79', 'template_lejaz39', e.target, 'user_ST3KnhWnIqNBJzctmQalH')
        .then((result) => {
            console.log(result.text);
        }, (error) => {
            console.log(error.text);
        });
    
    
     
      setTimeout(()=>{
          setLoad(false);
      },1000)
  
    
  }

    return(
        <div>
            <div className="container">
            <form onSubmit={sendEmail}>
                    <div className="row pt-5 mx-auto">
                        <div className="col-8 form-group mx-auto">
                            <input type="hidden" className="form-control" placeholder="Name" name="name" value="Muzammil"/>
                        </div>
                        <div className="col-8 form-group pt-2 mx-auto">
                            <input type="hidden" className="form-control" name="email" value="muzammilmanzoor14@gmail.com"/>
                        </div>
              
                        <div className="col-8 form-group pt-2 mx-auto">
                            <input type="hidden" className="form-control" name="brand" value=" khaadi "/>
                        </div>
                        <div className="col-8 pt-3 mx-auto">
                            <input type="submit" className="btn btn-info" value="Subscribe"></input>
                        </div>
                        <button type="submit" className="site-btn login-btn" onClick={submit} disabled={load}>{load && <i className="fa fa-refresh fa-spin"></i> } Sign In</button>

                    </div>
                </form>
            </div>
        </div>
    )
}
